<?php	/******************* Определяем кодирвку ************************/	header("Content-Type: text/html; charset=".SITE_CHARSET);$id = (int)$_GET["id"];$uid = (int)$_GET["uid"];// впишите код получения данных пользователя из базы по $idif($id>=MIN_CONSULTANT_ID){	$cons_id = $id - MIN_CONSULTANT_ID;	$foto = $TD_CONSULTANT[$cons_id]['FOTO'];	$name = $TD_CONSULTANT[$cons_id]['NAME'];	$dep = $TD_CONSULTANT[$cons_id]['DEPARTMENT'];	$link = '#';	}// впишите код получения данных пользователя из базы по $idelse if($id>=MIN_USER_ID && $id<=MIN_USER_ID){	$foto = AVATAR_ROOT_RELATIVE."/default.png";	$name = "Незнакомец";	$link = '#';	$dep = '';	}else{	$usr = get_user($id, 'id, name, login');	if($usr){		$files = glob( AVATAR_ROOT.'/'.$id.'.*');		if(!isset($files[0])) $foto = AVATAR_ROOT_RELATIVE.'/default.png';		else				  $foto =  AVATAR_ROOT_RELATIVE.'/'.basename($files[0]);				$name = $usr['name'];		if($name=='') $name = $usr['login'];		$link = url(NULL, 'USERS', 'about_user', 'user_id='.$usr['id']);		$dep = '';		}	else{		$foto = AVATAR_ROOT_RELATIVE."/default.png";		$name = "Незнакомец";		$link = '#';		$dep = '';		}	}if ($id===false)    $info = "<table width=100% height=100%><tr><th>Пользователь не существует либо был удален.</table>";else    $info = "<table cellspacing=0 cellpadding=0><tr><td rowspan=3>".            "<img src=".$foto." height=80 hspace=0>".            "<td style='padding:4px; color:blue; font-size:24px;'>".            "<a href='".$link."' target=_blank style='text-decoration:none;'>".$name."</a></td></tr>".			"<tr><td style='padding:4px; color:grey; font-size:12px;'>".			$dep.            "</td></tr></table>";echo "<style>body{font-family:tahoma;font-size:80%}</style>",     "<body bgcolor=#8DBBDC>", $info, "</body>";?> 